import useData from "./hooks/useData";
import Person from "./components/Person";
import Footer from "./components/Footer";
import { memo, useReducer } from "react";
import reducer from "./components/ReactReducers/reducer";

function App() {
  const { data } = useData()

  const initialState = [
    { name: 'waimiu', city: 'nairobi', email: 'fridah@gmail.com' },
    {name:'june',city:'nawe',email:'nawe@gmail.com'}
  ]

  const [state, dispatch] = useReducer(reducer, initialState)
  console.log(state)

  //Making sure that the footer component rerender only when something is different
  const MemorizedFooter = memo(Footer)
  return (
    <div className="App">
      <section>
        <h1>Persons Section</h1>
        {
        data.map((person,index) => {
          return <Person key={index} {...person} />
        })
      }
      </section>
      <section>
        <MemorizedFooter/>
      </section>
    </div>
  );
}

export default App;
